const COUNTDOWN_CONFIG={targetMonth:0,targetDay:18,updateInterval:1e3,localStorageKey:"countdownData"};function initializeTargetDate(){const currentYear=(new Date).getFullYear();let targetDate=new Date(currentYear,COUNTDOWN_CONFIG.targetMonth,COUNTDOWN_CONFIG.targetDay).getTime();const now=(new Date).getTime();if(now>targetDate){targetDate=new Date(currentYear+1,COUNTDOWN_CONFIG.targetMonth,COUNTDOWN_CONFIG.targetDay).getTime()}return targetDate}function formatNumber(number){return number.toString().padStart(2,"0")}function calculateTimeUnits(difference){return{days:Math.floor(difference/(1e3*60*60*24)),hours:Math.floor(difference%(1e3*60*60*24)/(1e3*60*60)),minutes:Math.floor(difference%(1e3*60*60)/(1e3*60)),seconds:Math.floor(difference%(1e3*60)/1e3)}}function updateDOM(timeUnits){const elements={days:document.getElementById("days"),hours:document.getElementById("hours"),minutes:document.getElementById("minutes"),seconds:document.getElementById("seconds")};Object.entries(timeUnits).forEach(([unit,value])=>{if(elements[unit]){elements[unit].textContent=formatNumber(value);elements[unit].classList.add("pulse");setTimeout(()=>elements[unit].classList.remove("pulse"),1e3)}})}function storeCountdownData(timeUnits){try{localStorage.setItem(COUNTDOWN_CONFIG.localStorageKey,JSON.stringify({...timeUnits,timestamp:(new Date).getTime()}))}catch(error){console.warn("Failed to store countdown data:",error)}}function loadCountdownData(){try{const data=localStorage.getItem(COUNTDOWN_CONFIG.localStorageKey);if(data){const parsedData=JSON.parse(data);const timeSinceLastUpdate=(new Date).getTime()-parsedData.timestamp;if(timeSinceLastUpdate<6e4){return parsedData}}}catch(error){console.warn("Failed to load countdown data:",error)}return null}function updateCountdown(){const targetDate=initializeTargetDate();const now=(new Date).getTime();const difference=targetDate-now;if(difference>0){const timeUnits=calculateTimeUnits(difference);updateDOM(timeUnits);storeCountdownData(timeUnits)}else{updateDOM({days:0,hours:0,minutes:0,seconds:0});localStorage.removeItem(COUNTDOWN_CONFIG.localStorageKey)}}function initializeCountdown(){const storedData=loadCountdownData();if(storedData){updateDOM(storedData)}updateCountdown();return setInterval(updateCountdown,COUNTDOWN_CONFIG.updateInterval)}const countdownInterval=initializeCountdown();window.addEventListener("beforeunload",()=>{clearInterval(countdownInterval)});document.querySelectorAll("button, a").forEach(element=>{element.addEventListener("click",function(){this.style.transform="scale(0.95)";setTimeout(()=>{this.style.transform=""},150)})});document.querySelectorAll(".course-item").forEach(item=>{item.addEventListener("mouseenter",function(){this.style.transform="translateX(10px)"});item.addEventListener("mouseleave",function(){this.style.transform="translateX(0)"})});document.querySelectorAll(".nav-button").forEach(button=>{button.addEventListener("click",function(){document.querySelectorAll(".nav-button").forEach(btn=>btn.classList.remove("active"));this.classList.add("active")})});window.addEventListener("load",function(){document.querySelector(".dashboard-container").style.animation="fadeIn 0.8s ease-out"});